template.code:
virtual size_t getNumMemberVariables() const
  {return getEnumeration()->getNumElements();}

virtual VariableSignaturePtr getMemberVariable(size_t index) const
  {return new VariableSignature(getVariablesType(), getEnumeration()->getElementName(index));}

virtual void createObjectVariables()
{
  EnumerationPtr enumeration = getEnumeration();
  size_t n = enumeration->getNumElements();
  TypePtr variablesType = getVariablesType();
  for (size_t i = 0; i < n; ++i)
    addMemberVariable(defaultExecutionContext(), variablesType, enumeration->getElementName(i));
}

EnumerationPtr getEnumeration() const
{
  jassert(getNumTemplateArguments() == 2);
  EnumerationPtr enumeration = getTemplateArgument(0).dynamicCast<Enumeration>();
  jassert(enumeration);
  return enumeration;
}

TypePtr getVariablesType() const
  {jassert(getNumTemplateArguments() == 2); return getTemplateArgument(1);}

virtual void createObjectVariables()
{
  jassert(getNumTemplateArguments() == 2);
  ClassPtr inputClass = getTemplateArgument(0).dynamicCast<Class>();
  TypePtr outputVariablesType = getTemplateArgument(1);
  jassert(inputClass);
  size_t n = inputClass->getNumMemberVariables();
  for (size_t i = 0; i < n; ++i)
    addMemberVariable(defaultExecutionContext(), outputVariablesType, inputClass->getMemberVariableName(i));
}

EnumerationPtr getEnumeration() const
  {return getTemplateArgument(0).dynamicCast<Enumeration>();}

virtual size_t getNumElements() const
  {return getEnumeration()->getNumElements() + 1;}

virtual EnumerationElementPtr getElement(size_t index) const
{
  EnumerationPtr enumeration = getEnumeration();
  if (index < enumeration->getNumElements())
    return enumeration->getElement(index);
  else
    return new EnumerationElement(T("missing"), T("_"), String::empty, T("Missing Value"));
}

EnumerationPtr getEnumeration() const
  {return getTemplateArgument(0).dynamicCast<Enumeration>();}

virtual size_t getNumElements() const
  {return getEnumeration()->getNumElements() + 1;}

virtual EnumerationElementPtr getElement(size_t index) const
{
  EnumerationPtr enumeration = getEnumeration();
  if (index < enumeration->getNumElements())
    return enumeration->getElement(index);
  else
    return new EnumerationElement(T("entropy"), String::empty, String::empty, T("Entropy"));
}

ClassPtr getTargetClass() const
  {return getTemplateArgument(0).dynamicCast<Class>();}

virtual size_t getNumElements() const
  {return getTargetClass()->getNumMemberVariables();}

virtual EnumerationElementPtr getElement(size_t index) const
{
  VariableSignaturePtr variable = getTargetClass()->getMemberVariable(index);
  return new EnumerationElement(variable->getName(), String::empty, variable->getShortName(), variable->getDescription());
}

class.code:
virtual size_t getNumElements() const
  {return 0;}
virtual EnumerationElementPtr getElement(size_t index) const
  {String str((int)index); return new EnumerationElement(str, String::empty, str);}
virtual int findElementByName(const String& name) const
  {return name.getIntValue();}
virtual String toString(const VariableValue& value) const
  {return String(value.getInteger());}
virtual VariableValue getMissingValue() const
  {return VariableValue(-1);}

template.code:
EnumerationPtr getFirstEnumeration() const
  {return getTemplateArgument(0).dynamicCast<Enumeration>();}

EnumerationPtr getSecondEnumeration() const
  {return getTemplateArgument(1).dynamicCast<Enumeration>();}

virtual size_t getNumElements() const
  {return getFirstEnumeration()->getNumElements() * getSecondEnumeration()->getNumElements();}

virtual EnumerationElementPtr getElement(size_t index) const
{
  if (index >= getNumElements())
    return new EnumerationElement(T("missing"), T("_"), String::empty, T("Missing Value"));

  EnumerationPtr firstEnumeration = getFirstEnumeration();
  EnumerationPtr secondEnumeration = getSecondEnumeration();
  size_t numSecondElements = secondEnumeration->getNumElements();
  EnumerationElementPtr firstElement = firstEnumeration->getElement(index / numSecondElements);
  EnumerationElementPtr secondElement = secondEnumeration->getElement(index % numSecondElements);

  return new EnumerationElement(T("(") + firstElement->getName() + T(", ") + secondElement->getName() + T(")"),
                                String::empty,
                                firstElement->getShortName() + T("x") + secondElement->getShortName(),
                                T("Cartesian Product (") + firstElement->getDescription() +
                                      T(", ") + secondElement->getDescription() + T(")"));
}
