template.code:
virtual size_t getNumElements(const VariableValue& value) const
  {return 2;}
virtual Variable getElement(const VariableValue& value, size_t index) const
{
  const PairPtr& pair = value.getObjectAndCast<Pair>();
  return pair ? (index == 0 ? pair->getFirst() : pair->getSecond()) : Variable();
}
virtual String getElementName(const VariableValue& value, size_t index) const
  {return index ? T("second") : T("first");}

      virtual size_t getNumElements(const VariableValue& value) const
      {
        const ContainerPtr& container = value.getObjectAndCast<Container>();
        return container ? container->getNumElements() : 0;
      }

      virtual Variable getElement(const VariableValue& value, size_t index) const
      {
        const ContainerPtr& container = value.getObjectAndCast<Container>();
        jassert(container);
        return container->getElement(index);
      }

      virtual String getElementName(const VariableValue& value, size_t index) const
        {return T("[") + String((int)index) + T("]");}

virtual size_t getObjectNumVariables() const
  {return getEnumeration()->getNumElements();}
  
virtual TypePtr getObjectVariableType(size_t index) const
  {return getVariablesType();}
  
virtual String getObjectVariableName(size_t index) const
  {return getEnumeration()->getElementName(index);}
  
virtual void createObjectVariables()
{
  EnumerationPtr enumeration = getEnumeration();
  size_t n = enumeration->getNumElements();
  TypePtr variablesType = getVariablesType();
  for (size_t i = 0; i < n; ++i)
    addVariable(defaultExecutionContext(), variablesType, enumeration->getElementName(i));
}
 
EnumerationPtr getEnumeration() const
{
  jassert(getNumTemplateArguments() == 2);
  EnumerationPtr enumeration = getTemplateArgument(0).dynamicCast<Enumeration>();
  jassert(enumeration);
  return enumeration;
}
   
TypePtr getVariablesType() const
  {jassert(getNumTemplateArguments() == 2); return getTemplateArgument(1);}

virtual void createObjectVariables()
{
  jassert(getNumTemplateArguments() == 2);
  ClassPtr inputClass = getTemplateArgument(0).dynamicCast<Class>();
  TypePtr outputVariablesType = getTemplateArgument(1);
  jassert(inputClass);
  size_t n = inputClass->getObjectNumVariables();
  for (size_t i = 0; i < n; ++i)
    addVariable(defaultExecutionContext(), outputVariablesType, inputClass->getObjectVariableName(i));
}
