template.code:
virtual size_t getNumElements(const VariableValue& value) const
  {return 2;}
virtual Variable getElement(const VariableValue& value, size_t index) const
{
  const PairPtr& pair = value.getObjectAndCast<Pair>();
  return pair ? (index == 0 ? pair->getFirst() : pair->getSecond()) : Variable();
}
virtual String getElementName(const VariableValue& value, size_t index) const
  {return index ? T("second") : T("first");}

class.code:
      virtual void destroy(VariableValue& value) const
        {Type::destroy(value);}

      virtual void copy(VariableValue& dest, const VariableValue& source) const
        {Type::copy(dest, source);}

      virtual void destroy(VariableValue& value) const
        {Type::destroy(value);}

      virtual void copy(VariableValue& dest, const VariableValue& source) const
        {Type::copy(dest, source);}

template.code:
virtual void createObjectVariables()
{
  jassert(getNumTemplateArguments() == 1);
  EnumerationPtr enumeration = getTemplateArgument(0).dynamicCast<Enumeration>();
  jassert(enumeration);
  size_t n = enumeration->getNumElements();
  for (size_t i = 0; i < n; ++i)
    addVariable(doubleType, enumeration->getElementName(i));
}

virtual void createObjectVariables()
{
  jassert(getNumTemplateArguments() == 2);
  ClassPtr inputClass = getTemplateArgument(0).dynamicCast<Class>();
  TypePtr outputVariablesType = getTemplateArgument(1);
  jassert(inputClass);
  size_t n = inputClass->getObjectNumVariables();
  for (size_t i = 0; i < n; ++i)
    addVariable(outputVariablesType, inputClass->getObjectVariableName(i));
}

      EnumerationPtr getEnumeration() const
        {return getTemplateArgument(0).staticCast<Enumeration>();}

      virtual size_t getObjectNumVariables() const
        {return getEnumeration()->getNumElements() + 1;}

      virtual Variable getObjectVariable(const Object* pthis, size_t index) const
      {
        const DiscreteProbabilityDistribution* distribution = dynamic_cast<const DiscreteProbabilityDistribution* >(pthis);
        return distribution ? Variable(distribution->values[index], probabilityType) : Variable::missingValue(probabilityType);
      }

      virtual void setObjectVariable(Object* pthis, size_t index, const Variable& subValue) const
      {
        DiscreteProbabilityDistribution* distribution = dynamic_cast<DiscreteProbabilityDistribution* >(pthis);
        if (distribution)
        {
          if (!distribution->values.size())
            distribution->values.resize(distribution->getEnumeration()->getNumElements() + 1, 0.0);

          jassert(subValue.isDouble());
          jassert(index < distribution->values.size());
          distribution->values[index] = subValue.getDouble();
        }
      }

      virtual TypePtr getObjectVariableType(size_t index) const
        {return probabilityType;}

      virtual String getObjectVariableName(size_t index) const
        {return T("p[") + Variable(index, getEnumeration()).toString() + T("]");}
