      InferencePtr regressionExtraTreeInference(ExecutionContext& context, const String& name, PerceptionPtr perception, size_t numTrees, size_t numAttributeSamplesPerSplit, size_t minimumSizeForSplitting)
      {
        InferencePtr decisionTreeModel = new RegressionBinaryDecisionTreeInference(name, perception);
        ProbabilityDistributionBuilderPtr probabilityBuilder = gaussianProbabilityDistributionBuilder();
        InferencePtr x3Inference = extraTreeInferenceLearner(numAttributeSamplesPerSplit, minimumSizeForSplitting, probabilityBuilder);
        return new ParallelVoteInference(context, name, numTrees, decisionTreeModel, x3Inference, probabilityBuilder);
      }
      
