      InferencePtr classificationExtraTreeInference(ExecutionContext& context, const String& name, PerceptionPtr perception, EnumerationPtr classes, size_t numTrees, size_t numAttributeSamplesPerSplit, size_t minimumSizeForSplitting)
      {
        InferencePtr decisionTreeModel = new ClassificationBinaryDecisionTreeInference(name, perception, classes);
        ProbabilityDistributionBuilderPtr probabilityBuilder = enumerationProbabilityDistributionBuilder(classes);
        InferencePtr x3Inference = extraTreeInferenceLearner(numAttributeSamplesPerSplit, minimumSizeForSplitting, probabilityBuilder);
        return new ParallelVoteInference(context, name, numTrees, decisionTreeModel, x3Inference, probabilityBuilder);
      }
